import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Phone, MapPin, Clock, DollarSign, MessageSquare, Star } from 'lucide-react';
import axios from 'axios';
import '../styles/ProfilePage.css';

const ProfilePage = () => {
  const { id } = useParams(); // Get service ID from URL
  const navigate = useNavigate(); // For back button

  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/services/${id}`)
      .then((res) => {
        setService(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching service:', err);
        setService(null);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="profile-page">Loading...</div>;
  if (!service) return <div className="profile-page">Service not found.</div>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <h1>{service.name}</h1>
        <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
      </div>

      <div className="profile-content">
        {/* Left Sidebar */}
        <div className="sidebar">
          <h2>Contact {service.name.split(' ')[0]}</h2>
          <p><Phone size={18} /> {service.contactPhone || 'N/A'}</p>
          <p><MapPin size={18} /> {service.location}</p>
          <p><Clock size={18} /> {service.availability || 'N/A'}</p>
          <p><DollarSign size={18} /> {service.priceRange || 'N/A'}</p>
          <div className="actions">
            <button className="chat"><MessageSquare size={18} /> Chat</button>
            <button className="call"><Phone size={18} /> Call</button>
          </div>
          <div className="rating-box">
            <Star size={28} fill="gold" stroke="gold" />
            <h3>{service.rating || 'N/A'}</h3>
            <p>Based on {service.reviews?.length || 0} reviews</p>
          </div>
        </div>

        {/* Right Section */}
        <div className="main-info">
          <section>
            <h2>About</h2>
            <p>{service.description}</p>
          </section>

          <section>
            <h2>Work Samples</h2>
            {service.workSamples?.length > 0 ? (
              <div className="samples">
                {service.workSamples.map((url, i) => (
                  <img
                    key={i}
                    src={url}
                    alt={`Sample ${i + 1}`}
                    onError={(e) => {
                      e.target.src = 'https://placehold.co/400x300/ccc/000?text=Image+Not+Found';
                    }}
                  />
                ))}
              </div>
            ) : (
              <p>No work samples available.</p>
            )}
          </section>

          <section>
            <h2>Customer Reviews</h2>
            {service.reviews?.length > 0 ? (
              service.reviews.map((r, i) => (
                <div key={i} className="review">
                  <strong>{r.user || 'Anonymous'}</strong>
                  <div className="review-stars">
                    {[...Array(r.rating)].map((_, i) => (
                      <Star key={i} size={16} fill="gold" stroke="gold" />
                    ))}
                    {[...Array(5 - r.rating)].map((_, i) => (
                      <Star key={i} size={16} stroke="gray" />
                    ))}
                  </div>
                  <p>{r.comment}</p>
                  <span>{r.date}</span>
                </div>
              ))
            ) : (
              <p>No reviews yet.</p>
            )}
            <button className="review-button">Leave a Review</button>
          </section>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
