import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Phone, MapPin, Clock, DollarSign, MessageSquare, Star } from 'lucide-react';
import '../styles/ProfilePage.css';
import { mockServices } from '../data/mockServices';

const ProfilePage = () => {
  const { id } = useParams(); // Get service ID from route
  const navigate = useNavigate(); // For back button

  const service = mockServices.find((s) => s.id === id); // Find service by ID

  if (!service) {
    return <div className="profile-page">Service not found.</div>;
  }

  return (

    <div className="profile-page">
      <button className="back-button" onClick={() => navigate(-1)}>← Back</button>
      <div className="profile-header">
        <h1>{service.name}</h1>
        <button className="back-button" onClick={() => navigate(-1)}>← Back</button>
      </div>

      <div className="profile-content">
        {/* Left Sidebar */}
        <div className="sidebar">
          <h2>Contact {service.name.split(' ')[0]}</h2>
          <p><Phone size={18} /> {service.contact}</p>
          <p><MapPin size={18} /> {service.location}</p>
          <p><Clock size={18} /> {service.availability}</p>
          <p><DollarSign size={18} /> {service.priceRange}</p>
          <div className="actions">
            <button className="chat"><MessageSquare size={18} /> Chat</button>
            <button className="call"><Phone size={18} /> Call</button>
          </div>
          <div className="rating-box">
            <Star size={28} fill="gold" stroke="gold" />
            <h3>{service.rating}</h3>
            <p>Based on {service.reviews.length} reviews</p>
          </div>
        </div>

        {/* Right Section */}
        <div className="main-info">
          <section>
            <h2>About</h2>
            <p>{service.description}</p>
          </section>

          <section>
            <h2>Work Samples</h2>
            {service.workSamples.length > 0 ? (
              <div className="samples">
                {service.workSamples.map((url, i) => (
                  <img
                    key={i}
                    src={url}
                    alt={`Sample ${i + 1}`}
                    onError={(e) => {
                      e.target.src = 'https://placehold.co/400x300/ccc/000?text=Image+Not+Found';
                    }}
                  />
                ))}
              </div>
            ) : <p>No work samples available.</p>}
          </section>

          <section>
            <h2>Customer Reviews</h2>
            {service.reviews.length > 0 ? (
              service.reviews.map((r) => (
                <div key={r.id} className="review">
                  <strong>{r.user}</strong>
                  <div className="review-stars">
                    {[...Array(r.rating)].map((_, i) => (
                      <Star key={i} size={16} fill="gold" stroke="gold" />
                    ))}
                    {[...Array(5 - r.rating)].map((_, i) => (
                      <Star key={i} size={16} stroke="gray" />
                    ))}
                  </div>
                  <p>{r.comment}</p>
                  <span>{r.date}</span>
                </div>
              ))
            ) : <p>No reviews yet.</p>}
            <button className="review-button">Leave a Review</button>
          </section>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
