// src/App.js
import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

// Page imports
import HomePage from './pages/HomePage';
import ListingsPage from './pages/ListingsPage';
import ProfilePage from './pages/ProfilePage';
import PostJobPage from './pages/PostJobPage';
import PostServicePage from './pages/PostServicePage';
import ViewJobsPage from './pages/ViewJobsPage';
import SignupPage from './pages/SignupPage';
import LoginPage from './pages/LoginPage';

const App = () => {
  const [services, setServices] = useState([]);
  const [user, setUser] = useState(null); // ðŸ”’ track logged-in user

  // âœ… Load logged-in user from localStorage if exists
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) setUser(JSON.parse(savedUser));
  }, []);

  // âœ… Fetch services from backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/services')
      .then(res => setServices(res.data))
      .catch(err => console.error('Failed to fetch services:', err));
  }, []);

  return (
    <Routes>
      <Route path="/" element={user ? <HomePage user={user} /> : <Navigate to="/login" />} />
      <Route path="/listings" element={<ListingsPage services={services} />} />
      <Route path="/profile/:id" element={<ProfilePage services={services} user={user} />} />
      <Route path="/post-job" element={user ? <PostJobPage user={user} /> : <Navigate to="/login" />} />
      <Route path="/jobs" element={<ViewJobsPage />} />
      <Route path="/post-service" element={user ? <PostServicePage user={user} /> : <Navigate to="/login" />} />
      <Route path="/signup" element={<SignupPage />} />
      <Route path="/login" element={<LoginPage setUser={setUser} />} />
    </Routes>
  );
};

export default App;
