import React, { useState } from 'react';
import HomePage from './pages/HomePage';
import ListingsPage from './pages/ListingsPage';
import ProfilePage from './pages/ProfilePage';
import PostJobPage from './pages/PostJobPage';
import PostServicePage from './pages/PostServicePage';
import { mockServices, serviceCategories } from './data/mockServices';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home'); // Options: home, listings, profile, post-job, post-service
  const [selectedService, setSelectedService] = useState(null);
  const [searchParams, setSearchParams] = useState({
    term: '',
    location: '',
    category: '',
  });

  const handleSearch = (term, location) => {
    setSearchParams({ term, location, category: '' });
    setCurrentPage('listings');
  };

  const handleCategorySelect = (category) => {
    setSearchParams({ term: '', location: '', category });
    setCurrentPage('listings');
  };

  const handleViewListings = (targetPage = 'listings') => {
    setSearchParams({ term: '', location: '', category: '' });
    setCurrentPage(targetPage);
  };

  const handleSelectService = (service) => {
    setSelectedService(service);
    setCurrentPage('profile');
  };

  const handleGoBack = () => {
    if (currentPage === 'profile') {
      setCurrentPage('listings');
    } else {
      setCurrentPage('home');
    }
  };

  return (
    <div>
      {currentPage === 'home' && (
        <HomePage
          onSearch={handleSearch}
          onCategorySelect={handleCategorySelect}
          onViewListings={handleViewListings}
        />
      )}
      {currentPage === 'listings' && (
        <ListingsPage
          services={mockServices}
          onSelectService={handleSelectService}
          initialCategory={searchParams.category}
          initialSearchTerm={searchParams.term}
          initialLocation={searchParams.location}
        />
      )}
      {currentPage === 'profile' && selectedService && (
        <ProfilePage service={selectedService} onGoBack={handleGoBack} />
      )}
      {currentPage === 'post-job' && (
        <PostJobPage onGoBack={handleGoBack} />
      )}
      {currentPage === 'post-service' && (
        <PostServicePage onGoBack={handleGoBack} />
      )}
    </div>
  );
};

export default App;
