import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfilePage = () => {
  const { token } = useContext(AuthContext);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(res.data);
      } catch (err) {
        setError('Failed to fetch profile.');
      }
    };

    fetchProfile();
  }, [token, navigate]);

  if (error) return <div className="text-red-500">{error}</div>;
  if (!profile) return <div className="text-center mt-10">Loading profile...</div>;

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 shadow-lg rounded-lg bg-white">
      <h2 className="text-2xl font-bold mb-6 text-center text-blue-600">My Profile</h2>
      <div className="space-y-4">
        <div>
          <span className="font-semibold">Name:</span> {profile.name}
        </div>
        <div>
          <span className="font-semibold">Email:</span> {profile.email}
        </div>
        <div>
          <span className="font-semibold">Phone:</span> {profile.phone}
        </div>
        <div>
          <span className="font-semibold">About:</span> {profile.about}
        </div>
        <div>
          <span className="font-semibold">Role:</span> {profile.role}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
