import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ViewJobsPage.css';
import { useNavigate } from 'react-router-dom';

const ViewJobsPage = () => {
  const [jobs, setJobs] = useState([]);
  const [expandedJobId, setExpandedJobId] = useState(null); // Track expanded job
  const navigate = useNavigate();
  useEffect(() => {
    axios.get('http://localhost:5000/api/jobs')
      .then(res => setJobs(res.data))
      .catch(err => console.error(err));
  }, []);

  const toggleDetails = (id) => {
    setExpandedJobId(prev => (prev === id ? null : id)); // Toggle
  };

  return (
    <div className="view-jobs-container">
      <h1>Job Listings</h1>
      <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
      {jobs.length > 0 ? (
        jobs.map(job => (
          <div key={job._id} className="job-card">
            <h3>{job.jobTitle}</h3>
            <p><strong>Location:</strong> {job.location}</p>
            <button onClick={() => toggleDetails(job._id)}>
              {expandedJobId === job._id ? 'Hide Details' : 'More Details'}
            </button>

            {expandedJobId === job._id && (
              <div className="job-details">
                <p><strong>Category:</strong> {job.category}</p>
                <p><strong>Description:</strong> {job.description}</p>
                <p><strong>Budget:</strong> {job.budget}</p>
                <p><strong>Preferred Date:</strong> {job.preferredDate}</p>
                <p><strong>Contact Name:</strong> {job.contactName}</p>
                <p><strong>Phone:</strong> {job.contactPhone}</p>
                {job.contactEmail && <p><strong>Email:</strong> {job.contactEmail}</p>}
                {job.imageFiles?.length > 0 && (
                  <div>
                    <strong>Images:</strong>
                    <ul>
                      {job.imageFiles.map((file, idx) => (
                        <li key={idx}>{file}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No jobs posted.</p>
      )}
    </div>
  );
};

export default ViewJobsPage;
