import React, { useState } from 'react';
import '../styles/FormPages.css';

const PostServicePage = ({ onGoBack }) => {
  const [serviceName, setServiceName] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');
  const [priceRange, setPriceRange] = useState('');
  const [availability, setAvailability] = useState('');
  const [contactPhone, setContactPhone] = useState('');
  const [contactEmail, setContactEmail] = useState('');
  const [profileImage, setProfileImage] = useState(null);
  const [workSamples, setWorkSamples] = useState([]);

  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleWorkSampleChange = (e) => {
    setWorkSamples(Array.from(e.target.files));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      serviceName,
      category,
      description,
      location,
      priceRange,
      availability,
      contactPhone,
      contactEmail,
      profileImage: profileImage?.name,
      workSamples: workSamples.map((f) => f.name),
    });
    alert('Service submitted!');
    onGoBack();
  };

  return (
    <div className="form-container">
      <button className="back-button" onClick={onGoBack}>← Back</button>
      <h1>List Your Service</h1>
      <form onSubmit={handleSubmit}>
        <label>Service Name*</label>
        <input type="text" value={serviceName} onChange={(e) => setServiceName(e.target.value)} required />

        <label>Primary Category*</label>
        <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} required />

        <label>Description*</label>
        <textarea rows="4" value={description} onChange={(e) => setDescription(e.target.value)} required />

        <label>Location*</label>
        <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} required />

        <label>Price Range</label>
        <input type="text" value={priceRange} onChange={(e) => setPriceRange(e.target.value)} />

        <label>Availability</label>
        <input type="text" value={availability} onChange={(e) => setAvailability(e.target.value)} />

        <fieldset>
          <legend>Contact Info</legend>
          <label>Phone*</label>
          <input type="tel" value={contactPhone} onChange={(e) => setContactPhone(e.target.value)} required />

          <label>Email</label>
          <input type="email" value={contactEmail} onChange={(e) => setContactEmail(e.target.value)} />
        </fieldset>

        <label>Profile Image</label>
        <input type="file" onChange={handleProfileImageChange} />
        {profileImage && <p>Selected: {profileImage.name}</p>}

        <label>Work Samples</label>
        <input type="file" multiple onChange={handleWorkSampleChange} />
        {workSamples.length > 0 && <p>{workSamples.length} file(s) selected</p>}

        <button type="submit" className="submit-button">List My Service</button>
      </form>
    </div>
  );
};

export default PostServicePage;
