import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Phone,
  MapPin,
  Clock,
  DollarSign,
  MessageSquare,
  Star,
  Mail,
  User
} from 'lucide-react';
import '../styles/ProfilePage.css';

const ProfilePage = () => {
  const { id } = useParams(); // Service ID
  const navigate = useNavigate();
  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/services/${id}`)
      .then(res => {
        setService(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError('Service not found.');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error || !service) return <p>{error || 'Service not found.'}</p>;

  const provider = service.userId || {};

  return (
    <div className="profile-page">
      <div className="profile-header">
        <h1>{service.name}</h1>
        <button className="back-button" onClick={() => navigate(-1)}>← Back</button>
      </div>

      <div className="profile-content">
        {/* Left Sidebar */}
        <div className="sidebar">
          <h2>Contact {provider.name || 'Provider'}</h2>
          <p><User size={18} /> {provider.name || 'N/A'}</p>
          <p><Phone size={18} /> {provider.phone || 'N/A'}</p>
          <p><Mail size={18} /> {provider.email || 'N/A'}</p>
          <p><MapPin size={18} /> {service.location}</p>
          <p><Clock size={18} /> {service.availability}</p>
          <p><DollarSign size={18} /> {service.priceRange}</p>

          <div className="actions">
            <button className="chat"><MessageSquare size={18} /> Chat</button>
            <button className="call"><Phone size={18} /> Call</button>
          </div>

          <div className="rating-box">
            <Star size={28} fill="gold" stroke="gold" />
            <h3>{service.rating || '4.5'}</h3>
            <p>Based on {(service.reviews?.length || 5)} reviews</p>
          </div>
        </div>

        {/* Right Section */}
        <div className="main-info">
          <section>
            <h2>About the Service</h2>
            <p>{service.description}</p>
          </section>

          <section>
            <h2>About the Provider</h2>
            <p>{provider.about || 'No about information available.'}</p>
          </section>

          <section>
            <h2>Work Samples</h2>
            {service.workSamples?.length > 0 ? (
              <div className="samples">
                {service.workSamples.map((name, i) => (
                  <img
                    key={i}
                    src={`https://placehold.co/400x300?text=${encodeURIComponent(name)}`}
                    alt={`Sample ${i + 1}`}
                  />
                ))}
              </div>
            ) : <p>No work samples available.</p>}
          </section>

          <section>
            <h2>Customer Reviews</h2>
            {service.reviews?.length > 0 ? (
              service.reviews.map((r, i) => (
                <div key={i} className="review">
                  <strong>{r.user || 'User'}</strong>
                  <div className="review-stars">
                    {[...Array(r.rating || 4)].map((_, i) => (
                      <Star key={i} size={16} fill="gold" stroke="gold" />
                    ))}
                  </div>
                  <p>{r.comment || 'No comment.'}</p>
                </div>
              ))
            ) : <p>No reviews yet.</p>}
            <button className="review-button">Leave a Review</button>
          </section>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
