import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ServiceCard from '../components/ServiceCard';
import '../styles/ListingsPage.css';
import { useNavigate } from 'react-router-dom';
import { Search, MapPin } from 'lucide-react';

const ListingsPage = () => {
  const [services, setServices] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/services')
      .then((response) => setServices(response.data))
      .catch((error) => console.error('Error fetching services:', error));
  }, []);

  const handleSearchClick = () => {
    const filtered = services.filter((service) => {
      const titleMatch = service.title.toLowerCase().includes(searchTerm.toLowerCase());
      const locationMatch = location === '' || service.location.toLowerCase().includes(location.toLowerCase());
      return titleMatch && locationMatch;
    });
    setFilteredServices(filtered);
  };

  const [filteredServices, setFilteredServices] = useState([]);

  useEffect(() => {
    setFilteredServices(services); // Initially show all
  }, [services]);

  const handleSelectService = (id) => {
    navigate(`/profile/${id}`);
  };

  return (
    <div className="listings-page">
      <div className="search-box">
        <div className="input-group">
          <Search className="icon" size={20} />
          <input
            type="text"
            placeholder="Search for a service (e.g., 'carpenter')"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearchClick()}
          />
        </div>

        <div className="input-group">
          <MapPin className="icon" size={20} />
          <input
            type="text"
            placeholder="Location (e.g., 'Erode')"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearchClick()}
          />
        </div>

        <button onClick={handleSearchClick}>Search Services</button>
      </div>

      <div className="service-grid">
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <div
              key={service._id}
              onClick={() => handleSelectService(service._id)}
              className="clickable-card"
            >
              <ServiceCard service={service} />
            </div>
          ))
        ) : (
          <p className="no-results">No services found matching your criteria.</p>
        )}
      </div>
    </div>
  );
};

export default ListingsPage;
