import React from 'react';
import { Briefcase, MapPin, Star } from 'lucide-react';
import '../styles/ServiceCard.css';

const ServiceCard = ({ service, onSelectService }) => {
  return (
    <div className="service-card" onClick={() => onSelectService(service)}>
      <div className="card-content">
        <h3>{service.name}</h3>
        <p><Briefcase size={16} /> {service.category}</p>
        <p><MapPin size={16} /> {service.location}</p>
        <div className="rating">
          <Star size={16} fill="gold" stroke="gold" />
          <span>{service.rating}</span>
          <span className="reviews">({service.reviews.length} reviews)</span>
        </div>
        <p className="description">{service.description}</p>
      </div>
      <div className="card-footer">
        <span className="price">{service.priceRange}</span>
        <button
          onClick={(e) => {
            e.stopPropagation();
            onSelectService(service);
          }}
        >
          View Profile
        </button>
      </div>
    </div>
  );
};

export default ServiceCard;
