import React, { useState } from 'react';
import axios from 'axios';
import '../styles/FormPages.css'; // You can reuse this styling
import { useNavigate } from 'react-router-dom';

const SignupPage = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    about: '',
    password: ''
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/signup', form);
      alert('Signup successful! Please login.');
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.error || 'Signup failed');
    }
  };

  return (
    <div className="form-container">
      <h1>Sign Up as Service Provider</h1>
      <form onSubmit={handleSubmit}>
        <label>Name*</label>
        <input name="name" type="text" value={form.name} onChange={handleChange} required />

        <label>Email*</label>
        <input name="email" type="email" value={form.email} onChange={handleChange} required />

        <label>Phone*</label>
        <input name="phone" type="text" value={form.phone} onChange={handleChange} required />

        <label>About You*</label>
        <textarea name="about" rows="3" value={form.about} onChange={handleChange} required />

        <label>Password*</label>
        <input name="password" type="password" value={form.password} onChange={handleChange} required />

        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default SignupPage;
